import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:5000");

const App = () => {
  const [slots, setSlots] = useState([]);
  const [userName, setUserName] = useState("");
  const [roomNumber, setRoomNumber] = useState("");
  const [purpose, setPurpose] = useState("");

  useEffect(() => {
    fetchSlots();

    socket.on("new_booking", (booking) => {
      setSlots((prev) => [...prev, booking]);
    });

    socket.on("cancel_booking", (id) => {
      setSlots((prev) => prev.filter((slot) => slot.id !== id));
    });

    return () => {
      socket.off("new_booking");
      socket.off("cancel_booking");
    };
  }, []);

  const fetchSlots = async () => {
    const res = await axios.get("http://localhost:5000/slots");
    setSlots(res.data);
  };

  const bookSlot = async () => {
    if (!roomNumber || !userName || !purpose) return alert("Enter all details");
    await axios.post("http://localhost:5000/book", { room_number: roomNumber, user_name: userName, purpose });
    setRoomNumber("");
    setUserName("");
    setPurpose("");
  };

  const cancelBooking = async (id) => {
    await axios.post("http://localhost:5000/cancel", { id });
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Room Slot Booking</h1>
      <div>
        <input type="text" placeholder="Your Name" value={userName} onChange={(e) => setUserName(e.target.value)} />
        <input type="number" placeholder="Room Number" value={roomNumber} onChange={(e) => setRoomNumber(e.target.value)} />
        <input type="text" placeholder="Purpose" value={purpose} onChange={(e) => setPurpose(e.target.value)} />
        <button onClick={bookSlot}>Book Slot</button>
      </div>

      <h2>Available Slots</h2>
      <ul>
        {slots.length === 0 ? <p>No slots booked</p> : slots.map((slot) => (
          <li key={slot.id}>
            <strong>Room {slot.room_number}</strong> booked by <strong>{slot.user_name}</strong> for <i>{slot.purpose}</i>
            <button onClick={() => cancelBooking(slot.id)}>Cancel</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
