import React, { useEffect, useState } from "react";
import axios from "axios";
import io from "socket.io-client";
import moment from "moment-timezone";

const socket = io("http://localhost:5000");

const App = () => {
  const [bookings, setBookings] = useState([]);
  const [room, setRoom] = useState("");
  const [name, setName] = useState("");
  const [purpose, setPurpose] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/slots").then((res) => setBookings(res.data));
    socket.on("new_booking", (data) => {
      setBookings((prev) => [data, ...prev]);
    });
  }, []);

  const bookSlot = async () => {
    try {
      await axios.post("http://localhost:5000/book", {
        room_name: room,
        user_name: name,
        purpose,
        booking_date: date,
        booking_time: time,
      });
    } catch (error) {
      alert(error.response.data.error);
    }
  };

  return (
    <div>
      <h2>Room Booking</h2>
      <label>Select Room:</label>
      <select onChange={(e) => setRoom(e.target.value)}>
        <option value="">Select</option>
        <option value="Akasha">Akasha</option>
        <option value="Vayu">Vayu</option>
        <option value="Agni">Agni</option>
        <option value="Main Conference Hall">Main Conference Hall</option>
      </select>
      <input placeholder="Your Name" onChange={(e) => setName(e.target.value)} />
      <input placeholder="Purpose" onChange={(e) => setPurpose(e.target.value)} />
      <input type="date" onChange={(e) => setDate(e.target.value)} />
      <input type="time" onChange={(e) => setTime(e.target.value)} />
      <button onClick={bookSlot}>Book Slot</button>

      <h3>Bookings</h3>
      <ul>
        {bookings.map((b) => (
          <li key={b.id}>
            {b.room_name} - {b.user_name} - {b.purpose} - {moment(b.booking_datetime).tz("Asia/Kolkata").format("LLLL")}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
