import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

const ROOMS = ["Akasha", "Vayu", "Agni", "Main Conference Hall"];

function App() {
  const [bookings, setBookings] = useState([]);
  const [formData, setFormData] = useState({
    user_name: "",
    purpose: "",
    room_name: "",
    booking_date: "",
    start_time: "",
    end_time: "",
  });

  useEffect(() => {
    fetchBookings();
    socket.on("new_booking", (newBooking) => setBookings((prev) => [newBooking, ...prev]));
    socket.on("cancel_booking", (id) => setBookings((prev) => prev.filter((b) => b.id !== id)));
  }, []);

  const fetchBookings = async () => {
    try {
      const res = await axios.get("http://localhost:5000/slots");
      setBookings(res.data);
    } catch {
      alert("Failed to fetch bookings");
    }
  };

  const bookSlot = async () => {
    try {
      const res = await axios.post("http://localhost:5000/book", formData);
      setFormData({ user_name: "", purpose: "", room_name: "", booking_date: "", start_time: "", end_time: "" });
    } catch {
      alert("Booking failed");
    }
  };

  return (
    <div className="container">
      <h2>Room Booking System</h2>

      <input type="text" placeholder="Your Name" value={formData.user_name} onChange={(e) => setFormData({ ...formData, user_name: e.target.value })} />
      <input type="text" placeholder="Purpose" value={formData.purpose} onChange={(e) => setFormData({ ...formData, purpose: e.target.value })} />
      <input type="date" value={formData.booking_date} onChange={(e) => setFormData({ ...formData, booking_date: e.target.value })} />
      <input type="time" value={formData.start_time} onChange={(e) => setFormData({ ...formData, start_time: e.target.value })} />
      <input type="time" value={formData.end_time} onChange={(e) => setFormData({ ...formData, end_time: e.target.value })} />

      {ROOMS.map((room) => (
        <label key={room}>
          <input type="radio" name="room" value={room} onChange={(e) => setFormData({ ...formData, room_name: e.target.value })} />
          {room}
        </label>
      ))}

      <button onClick={bookSlot}>Book Room</button>

      <h3>Bookings:</h3>
      <ul>{bookings.map((b) => <li key={b.id}>{b.user_name} booked {b.room_name} on {b.booking_date}</li>)}</ul>
    </div>
  );
}

export default App;
