import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

function App() {
  const [bookings, setBookings] = useState([]);
  const [formData, setFormData] = useState({
    room_name: "Akasha",
    user_name: "",
    purpose: "",
    booking_date: "",
    start_time: "",
    end_time: "",
  });

  useEffect(() => {
    fetchBookings();
    socket.on("new_booking", (newBooking) => {
      setBookings((prev) => [...prev, newBooking]);
    });
    socket.on("cancel_booking", (id) => {
      setBookings((prev) => prev.filter((b) => b.id !== id));
    });
  }, []);

  const fetchBookings = async () => {
    const res = await axios.get("http://localhost:5000/slots");
    setBookings(res.data);
  };

  const bookSlot = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/book", formData);
      setFormData({ room_name: "Akasha", user_name: "", purpose: "", booking_date: "", start_time: "", end_time: "" });
    } catch (error) {
      alert(error.response.data.error);
    }
  };

  const cancelBooking = async (id) => {
    await axios.post("http://localhost:5000/cancel", { id });
  };

  return (
    <div style={{ display: "flex", justifyContent: "space-between" }}>
      <form onSubmit={bookSlot}>
        <h2>Book a Room</h2>
        <select onChange={(e) => setFormData({ ...formData, room_name: e.target.value })}>
          <option>Akasha</option>
          <option>Vayu</option>
          <option>Agni</option>
          <option>Main Conference Hall</option>
        </select>
        <input placeholder="Your Name" onChange={(e) => setFormData({ ...formData, user_name: e.target.value })} />
        <input placeholder="Purpose" onChange={(e) => setFormData({ ...formData, purpose: e.target.value })} />
        <input type="date" onChange={(e) => setFormData({ ...formData, booking_date: e.target.value })} />
        <input type="time" onChange={(e) => setFormData({ ...formData, start_time: e.target.value })} />
        <input type="time" onChange={(e) => setFormData({ ...formData, end_time: e.target.value })} />
        <button type="submit">Book</button>
      </form>
      <div>
        <h2>Booked Slots</h2>
        {bookings.map((b) => (
          <div key={b.id}>
            {b.room_name} - {b.booking_date} {b.start_time} - {b.end_time} ({b.user_name}, {b.purpose})
            <button onClick={() => cancelBooking(b.id)}>Cancel</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
