import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";

// Create socket connection
const socket = io("http://localhost:5000");

function App() {
  const [bookings, setBookings] = useState([]);
  const [formData, setFormData] = useState({
    room_name: "Akasha",
    user_name: "",
    purpose: "",
    booking_date: "",
    start_time: "",
    end_time: "",
  });

  // Fetch bookings on mount and setup socket listeners
  useEffect(() => {
    fetchBookings();

    socket.on("new_booking", (newBooking) => {
      // Check if the booking already exists
      setBookings((prev) => {
        if (!prev.some((booking) => booking.id === newBooking.id)) {
          return [...prev, newBooking];
        }
        return prev;
      });
    });

    socket.on("cancel_booking", (id) => {
      setBookings((prev) => prev.filter((b) => b.id !== id));
    });

    // Cleanup socket listeners on unmount
    return () => {
      socket.off("new_booking");
      socket.off("cancel_booking");
    };
  }, []);

  const fetchBookings = async () => {
    const res = await axios.get("http://localhost:5000/slots");
    setBookings(res.data);
  };

  const bookSlot = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/book", formData);
      setFormData({
        room_name: "Akasha",
        user_name: "",
        purpose: "",
        booking_date: "",
        start_time: "",
        end_time: "",
      });
    } catch (error) {
      alert(error.response?.data?.error || "Error booking slot");
    }
  };

  const cancelBooking = async (id) => {
    await axios.post("http://localhost:5000/cancel", { id });
  };

  return (
    <div style={{ display: "flex", justifyContent: "space-between", padding: "20px" }}>
      <form onSubmit={bookSlot} style={{ width: "40%" }}>
        <h2>Book a Room</h2>
        <select
          value={formData.room_name}
          onChange={(e) => setFormData({ ...formData, room_name: e.target.value })}
        >
          <option>Akasha</option>
          <option>Vayu</option>
          <option>Agni</option>
          <option>Main Conference Hall</option>
        </select>
        <input
          type="text"
          placeholder="Your Name"
          value={formData.user_name}
          onChange={(e) => setFormData({ ...formData, user_name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Purpose"
          value={formData.purpose}
          onChange={(e) => setFormData({ ...formData, purpose: e.target.value })}
        />
        <input
          type="date"
          value={formData.booking_date}
          onChange={(e) => setFormData({ ...formData, booking_date: e.target.value })}
        />
        <input
          type="time"
          value={formData.start_time}
          onChange={(e) => setFormData({ ...formData, start_time: e.target.value })}
        />
        <input
          type="time"
          value={formData.end_time}
          onChange={(e) => setFormData({ ...formData, end_time: e.target.value })}
        />
        <button type="submit">Book</button>
      </form>

      <div style={{ width: "50%" }}>
        <h2>Booked Slots</h2>
        <div style={{ maxHeight: "400px", overflowY: "scroll" }}>
          {bookings.map((b) => (
            <div key={b.id} style={{ marginBottom: "10px", borderBottom: "1px solid #ddd", padding: "10px" }}>
              <div>{b.room_name} - {b.booking_date} {b.start_time} - {b.end_time}</div>
              <div>({b.user_name}, {b.purpose})</div>
              <button onClick={() => cancelBooking(b.id)}>Cancel</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
